import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-tts',
  templateUrl: './tts.component.html',
  styleUrls: ['./tts.component.css']
})
export class TtsComponent implements OnInit{
  public sourceText!: string

  ngOnInit(): void {
      this.sourceText = ''
  }


}
<div class="red-border">
  <p>tts works!</p>
  <div class="d-flex flex-column justify-content-center align-content-center">

    <textarea [(ngModel)]="sourceText" class="w-50 mx-auto mb-3" style="font-size: 12px">{{ sourceText }}</textarea>

    <button (click)="getVoiceFile()" class="w-50 mx-auto">Envoyer</button>

    <div class="audio-container">
      <audio [src]="" controls autoplay></audio>
    </div>

  </div>
</div>
