import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-inscription',
  templateUrl: './inscription.component.html',
  styleUrls: ['./inscription.component.css']
})
export class InscriptionComponent implements OnInit {
  public langues: string[] = [
    'Français',
    'English'
  ]

  public monFormulaire!: FormGroup
  public email!: FormControl
  public password!: FormControl
  public confirmPassword!: FormControl
  public language!: FormControl


  ngOnInit(): void {
    this.createFormControls();
    this.createFormModel();
  }

  createFormControls() {

    this.email = new FormControl('', [Validators.required, Validators.pattern("[^ @]*@[^ @]*")]);

    this.password = new FormControl('', [Validators.required, Validators.minLength(8)]);

    this.confirmPassword = new FormControl('', [Validators.required, Validators.minLength(8)]);

    this.language = new FormControl('', Validators.required);

    console.log(this.language);
  }

  createFormModel () {

    this.monFormulaire = new FormGroup({

      email: this.email,

      password: this.password,

      confirmPassword: this.confirmPassword,

      language: this.language,

   });

}


}
<div class="green-border">
  <p>inscription works!</p>
  <form [formGroup]="monFormulaire" novalidate>

    <div class="form-group">
      <label for="email">Email:</label>
      <input
        id="email"
        type="text"
        class="form-control"
        formControlName="email"
      >
    </div>

    <div class="form-group">
      <label for="password">Password:</label>
      <input
        id="password"
        type="password"
        class="form-control"
        formControlName="password"
      >
    </div>

    <div class="form-group">
      <label for="confirmPassword">Confirm password:</label>
      <input
        id="confirmPassword"
        type="password"
        class="form-control"
        formControlName="confirmPassword"
      >
    </div>

    <select
      id="language"
      class="form-control"
      formControlName="language"
    >
      <option value="choose">--Sélectionnez votre langue</option>
      <option *ngFor="let langue of langues" [value]="langue">{{ langue }}</option>

    </select>

     <button type="submit" class="btn btn-primary">Envoyer</button>

     <pre>{{monFormulaire.value | json}}</pre>
  </form>
</div>
