import { Component, OnInit } from '@angular/core';
import { EmployeService } from 'src/app/services/employe-service.service';

@Component({
  selector: 'app-parent',
  templateUrl: './parent.component.html',
  styleUrls: ['./parent.component.css']
})

export class ParentComponent implements OnInit{

  public data!: any
  public donneeCliquee!: string

  constructor(private readonly service: EmployeService) {}

  ngOnInit(): void {
      this.data = {
        nom: "Dubois",
        prenom: "jean-claude",
        fonction: "Manager",
      }
  }

  receptionEvenement(ev:string) {
    console.log(ev)
    this.donneeCliquee = ev
  }
}
<div class="purple-border">
  <p>parent works!</p>
  <small>vous avez cliqué sur: {{ donneeCliquee | ucfirst }}</small>
  <app-enfant [tuyauData]="data" (emetteur)="receptionEvenement($event)"/>
</div>
