import { Component, OnInit, Input, Output, EventEmitter } from '@angular/core';

@Component({
  selector: 'app-enfant',
  templateUrl: './enfant.component.html',
  styleUrls: ['./enfant.component.css']
})
export class EnfantComponent implements OnInit{

  @Input() tuyauData: any
  @Output() emetteur: EventEmitter<string> = new EventEmitter<string>()

  ngOnInit(): void {
      console.log(this.tuyauData)
  }

  transmettre(donneeAEnvoyer: string){
    this.emetteur.emit(donneeAEnvoyer)
  }
}
<div class="violet-border">
  <p>enfant works!</p>
  <p (click)="transmettre(tuyauData.nom)">Nom: {{ tuyauData.nom }}</p>
  <p (click)="transmettre(tuyauData.prenom)">Prénom: {{ tuyauData.prenom | ucfirst }}</p>
  <p (click)="transmettre(tuyauData.fonction)">Fonction: {{ tuyauData.fonction }}</p>
</div>
