import { Component, OnInit } from '@angular/core';
import { TtsService } from 'src/app/services/tts.service';

@Component({
  selector: 'app-tts',
  templateUrl: './tts.component.html',
  styleUrls: ['./tts.component.css']
})
export class TtsComponent implements OnInit{
  public sourceText!: string


  constructor(
    private readonly ttsService: TtsService
  ){}

  ngOnInit(): void {
      this.sourceText = ''
  }

  getVoiceFile() {
    this.ttsService.setSource(this.sourceText)
    this.ttsService.getAudioFile(this.ttsService.queryParts).subscribe({
      next: (res: any) => {
        console.log(res)
      }
    })
  }
}
<div class="red-border">
  <p>tts works!</p>
  <div class="d-flex flex-column justify-content-center align-content-center">

    <textarea [(ngModel)]="sourceText" class="w-50 mx-auto mb-3" style="font-size: 12px">{{ sourceText }}</textarea>

    <button (click)="getVoiceFile()" class="w-50 mx-auto">Envoyer</button>

    <div class="audio-container">
      <audio class=" d-block mx-auto mt-5" [src]="" controls autoplay></audio>
    </div>

  </div>
</div>
