import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-hero',
  templateUrl: './hero.component.html',
  styleUrls: ['./hero.component.css']
})
export class HeroComponent {
  @Input() headerData!: any

}
<div class=" container d-flex flex-column justify-content-center align-items-center overflow-hidden position-relative hero-container">
  <img [src]="headerData.heroImg" alt="hero">
  <p class="hero-title">{{ headerData.heroTitle }}</p>
</div>
