import { Component, OnInit } from '@angular/core';
import { EmployeModel } from 'src/app/models/employe-model.model';
import { EmployeService } from 'src/app/services/employe-service.service';

@Component({
  selector: 'app-liste-employes',
  templateUrl: './liste-employes.component.html',
  styleUrls: ['./liste-employes.component.css']
})
export class ListeEmployesComponent implements OnInit{
  public listeEmployes!: EmployeModel[]

  constructor(
    private employeService: EmployeService
  ){}

  ngOnInit(): void {
      this.listeEmployes = this.employeService.getEmployeList()
      console.log("liste employes depuis l'afficheur", this.listeEmployes)
  }
}
<div class="red-border">
  <p>liste-employes works!</p>

  <ul *ngFor="let e of listeEmployes; let idx = index; let premier = first; let dernier = last; let impaire = odd; let paire = even">
    <li *ngIf="premier">Premier Employé!</li>
    <li>Nom: {{ e.nom }}</li>
    <li>Prénom: {{ e.prenom }}</li>
    <li>Fonction: {{ e.fonction }}</li>
  </ul>

</div>
